mapper.select:
SELECT COUNT(*) FROM BOARD
WHERE COMMUNITY_CODE = #{boardCode}
AND BOARD_DEL_FL = 'N'

SELECT COUNT(*) FROM "LIKE"
WHERE BOARD_NO = ${boardNo} 
AND MEMBER_NO = ${memberNo}

mapper.delete:
DELETE FROM "LIKE"
WHERE BOARD_NO = ${boardNo}
AND MEMBER_NO = ${memberNo}

mapper.update:
	UPDATE BOARD SET
    BOARD_COUNT = BOARD_COUNT + 1
	WHERE BOARD_NO = ${boardNo}

mapper.select:
SELECT * FROM BOARD_IMG
WHERE BOARD_NO = ${boardNo}
ORDER BY IMG_ORDER
