mapper.select:
SELECT B.BOARD_NO, B.BOARD_TITLE, B.B_CREATE_DATE, B.BOARD_COUNT, G.GENRE_NAME,
TO_CHAR(P.PM_START_TIME, 'YYYY.MM.DD') PM_START_TIME, TO_CHAR(P.PM_END_TIME, 'YYYY.MM.DD') PM_END_TIME, P.PM_PLAYTIME, H.PM_HOUSE_NAME, H.PM_HOUSE_ADDRESS, H.PM_HOUSE_LAT, H.PM_HOUSE_LONG, H.PM_HOUSE_WEBSITE, H.PM_HOUSE_HOME,
(SELECT COUNT(*) FROM "LIKE" L WHERE L.BOARD_NO = B.BOARD_NO) LIKE_COUNT, (SELECT COUNT(*) FROM "COMMENT" C WHERE C.BOARD_NO = B.BOARD_NO AND COMMENT_DEL_FL = 'N') COMMENT_COUNT FROM "BOARD" B JOIN PM P ON(B.BOARD_NO = P.BOARD_NO) JOIN PM_HOUSE H ON(P.PM_HOUSE_NO = H.PM_HOUSE_NO) JOIN GENRE G ON(P.GENRE_NO = G.GENRE_NO) WHERE B.BOARD_DEL_FL = 'N' AND B.COMMUNITY_CODE = 4 AND B.BOARD_NO = #{boardNo}

SELECT COUNT(*) 
FROM "LIKE"
WHERE BOARD_NO = ${boardNo}
AND MEMBER_NO = ${memberNo}

mapper.delete:
DELETE FROM "LIKE"
WHERE BOARD_NO = #{boardNo}
AND MEMBER_NO = #{memberNo}

mapper.update:
UPDATE BOARD
SET BOARD_COUNT = BOARD_COUNT + 1
WHERE BOARD_NO = #{boardNo}

mapper.select:
		SELECT COUNT(*) 
		FROM BOARD B
		
		JOIN PM P ON B.BOARD_NO = P.BOARD_NO
		
		
		
			LEFT JOIN (
	             		SELECT BOARD_NO, MIN(PM_PRICE) AS MIN_PRICE
	             		FROM PM_PRICE_LIST
	             		GROUP BY BOARD_NO
	         		) PR ON PR.BOARD_NO = B.BOARD_NO
        
        
		
  			JOIN PM_HOUSE H ON H.PM_HOUSE_NO = P.PM_HOUSE_NO
		
		
		WHERE B.COMMUNITY_CODE = 4
		AND B.BOARD_DEL_FL = 'N'
		
		
		
			AND P.GENRE_NO IN
			
        		#{type}
      		
		
		
		
		
		
			
      		
        		AND PR.MIN_PRICE BETWEEN #{price.min} AND #{price.max}
      		
      		
      		
        		AND PR.MIN_PRICE >= #{price.min}
      		
      		
      		
        		AND PR.MIN_PRICE <= #{price.max}
      		
    	
    	
    	
    	
      		AND P.PM_START_TIME BETWEEN #{date.startDate} AND #{date.endDate}
    	
    	
    	
    	
      		AND (
      		
        		H.PM_HOUSE_ADDRESS LIKE '%' || #{a} || '%'
      		
      		)
    	
    	
    	
    	
      		AND B.BOARD_TITLE LIKE '%' || #{query} || '%'

SELECT B.BOARD_NO, B.BOARD_TITLE, B.B_CREATE_DATE, B.BOARD_COUNT, H.PM_HOUSE_NAME, H.PM_HOUSE_ADDRESS, TO_CHAR(P.PM_START_TIME, 'YYYY.MM.DD') PM_START_TIME, TO_CHAR(P.PM_END_TIME, 'YYYY.MM.DD') PM_END_TIME, (SELECT IMG_PATH FROM BOARD_IMG I WHERE I.BOARD_NO = B.BOARD_NO AND IMG_ORDER = 0) POSTER
FROM "BOARD" B JOIN PM P ON(B.BOARD_NO = P.BOARD_NO) JOIN PM_HOUSE H ON(P.PM_HOUSE_NO = H.PM_HOUSE_NO)
LEFT JOIN ( SELECT BOARD_NO, MIN(PM_PRICE) AS MIN_PRICE FROM PM_PRICE_LIST GROUP BY BOARD_NO ) PR ON PR.BOARD_NO = B.BOARD_NO
WHERE B.BOARD_DEL_FL = 'N' AND B.COMMUNITY_CODE = 4
AND P.GENRE_NO IN
#{type}
AND PR.MIN_PRICE BETWEEN #{price.min} AND #{price.max}
AND PR.MIN_PRICE >= #{price.min}
AND PR.MIN_PRICE <= #{price.max}
AND P.PM_START_TIME BETWEEN #{date.startDate} AND #{date.endDate}
AND (
H.PM_HOUSE_ADDRESS LIKE '%' || #{a} || '%'
)
AND B.BOARD_TITLE LIKE '%' || #{query} || '%'
ORDER BY B.B_CREATE_DATE DESC
