mapper.select:
SELECT COUNT(*) FROM NOTIFICATION
WHERE NOTIFICATION_CHECK = 'N'
AND RECEIVE_MEMBER_NO = ${memberNo}

mapper.update:
		UPDATE NOTIFICATION
		SET NOTIFICATION_CHECK = 'Y'
		WHERE NOTIFICATION_NO = #{notificationNo}

mapper.select:
SELECT MEMBER_NO
FROM BOARD
WHERE BOARD_NO = #{boardNo}
